create or replace procedure i213_placeorder(order_val numeric,prods_id int[],cpn_code varchar(100),order_id int)
language plpgsql
as
$body$
declare
cpn_id int;
cpn_value numeric;
fin_ord_val numeric;
begin
SELECT dcpn_id, dcpn_value
    INTO cpn_id, cpn_value
    FROM i213_discountcoupon
    WHERE dcpn_code = cpn_code;
fin_ord_val=orderval-cpn_val;
insert into i213_ordersdata (order_value,prod_id,dcpnx_id) values(fin_ord_val,prods_id,cpn_id) returning ord_id into order_id;
end;
$body$

create or replace function updatealldata()
returns trigger
language plpgsql
as
$body$
begin
if new.dcpn_id>0 then
 update i213_discountcoupon set dcpn_count=dcpncount-1 where dcpn_id=new.dcpn_id;
end if;
for i in 1..ARRAY_LENGTH(new.prod_id,1) LOOP
update i213_ProductStock set prod_stock=prod_stock-1 where prod_id=new.prod_id[i];
end loop;
return new;
end;
$body$

create or replace trigger update_data_trigger
after insert on i213_ordersdata 
for each statement
execute function updatealldata();